import axios from 'axios'
import React, { Component } from 'react'
import { Col, ListGroup } from 'react-bootstrap'
import { API_URL } from '../utils/constants';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCheese, faCoffee, faUtensils } from '@fortawesome/free-solid-svg-icons';

const Icon = ({ nama }) => {
  if (nama === "Makanan") return <FontAwesomeIcon icon={faUtensils} className='mr-2' />
  if (nama === "Minuman") return <FontAwesomeIcon icon={faCoffee} />
  if (nama === "Cemilan") return <FontAwesomeIcon icon={faCheese} className='mr-2' />
  return <FontAwesomeIcon icon={faUtensils} className='mr-2' />
}




export default class ListCategories extends Component {
  constructor(props) {
    super(props)

    this.state = {
      categories: []
    }
  }

  componentDidMount() {
    axios
      .get(API_URL + "categories")
      .then(res => {

        const categories = res.data;
        this.setState({ categories });
      })
      .catch(error => {
        console.log(error)
          ;
      })
  }

  render() {
    const { categories } = this.state
    const {changeCategory,categoriYangDipilih} = this.props
    return (
      <Col md={2} mt="3">
        <h4><strong>Daftar kategori</strong></h4>
        <hr />
        <ListGroup>
          {categories && categories.map((category) => (
            <ListGroup.Item key={category.id} onClick={() => changeCategory(category.nama)}>
              <h5>
                <Icon nama={category.nama}/>{category.nama}
              </h5>
            </ListGroup.Item>
          ))}
        </ListGroup>
      </Col>
    )
  }
}
