import { Col, Container, Row } from 'react-bootstrap';
import { Hasil, ListCategories, Menus, NavbarComponent } from './components';
import React, { Component } from 'react'
import { API_URL } from './utils/constants';
import axios from 'axios';
import Swal from 'sweetalert2';




export default class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
      menus: [],
      categoriYangDipilih: "Makanan"
    }
  }

  componentDidMount() {
    axios
      .get(API_URL + "products?category.nama=" + this.state.categoriYangDipilih)
      .then(res => {

        const menus = res.data;
        this.setState({ menus });
      })
      .catch(error => {
        console.log(error)
          ;
      })
  }


  changeCategory = (value) => {
    this.setState({
      categoriYangDipilih: value,
      menus: []
    })
    axios
      .get(API_URL + "products?category.nama=" + value)
      .then(res => {

        const menus = res.data;
        this.setState({ menus });
      })
      .catch(error => {
        console.log(error)
          ;
      })

  }
  masukKeranjang = (value) => {

    axios
      .get(API_URL + "keranjangs?product.id=" + value.id)
      .then(res => {

        if (res.data.length === 0) {
          const keranjang = {
            jumlah: 1,
            total_harga: value.harga,
            product: value
          }

          axios
            .post(API_URL + "keranjangs", keranjang)
            .then(res => {
              Swal.fire({
                title: "Mantap",
                text: keranjang.product.nama + " masuk ke Keranjang",
                icon: "success",
                button: false,
                timer:3000
              });

            })
            .catch(error => {
              console.log(error)
                ;
            })
        }

        else {
          const keranjang = {
            jumlah: res.data[0].jumlah + 1,
            total_harga: res.data[0].total_harga + value.harga,
            product: value
          }


        axios
          .put(API_URL + "keranjangs/" + res.data[0].id, keranjang)
          .then(res => {
            Swal.fire({
              title: "Mantap",
              text: keranjang.product.nama + " masuk ke Keranjang",
              icon: "success",
              button: false,
              timer: 1000
            });

          })
          .catch(error => {
            console.log(error)
              ;
          })
        }
      })
      .catch(error => {
        console.log(error)
          ;
      })



  }




  render() {
    const { menus, categoriYangDipilih } = this.state
    return (
      <div className='App'>
        <NavbarComponent />
        <div className='mt-3'>
          <Container fluid>
            <Row>
              <ListCategories changeCategory={this.changeCategory} categoriYangDipilih={categoriYangDipilih} />
              <Col>
                <h4>
                  <strong>Daftar Product</strong>
                </h4>
                <hr />
                <Row>
                  {menus && menus.map((menu) => (
                    <Menus
                      key={menu.id}
                      menu={menu}
                      masukKeranjang={this.masukKeranjang}
                    />
                  ))}
                </Row>
              </Col>
              <Hasil />
            </Row>
          </Container>
        </div>
      </div>
    )
  }
}
